mapper.select:
SELECT 

FROM agency_houses_agent_assoc T

WHERE T.id = #{id}

mapper.update:
UPDATE agency_houses_agent_assoc SET 	
	housesourceid = #{housesourceid},
	userid = #{userid},
	subsidyvalue = #{subsidyvalue}
WHERE id = #{id}

update agency_houses_agent_assoc set 
	deleted=1
WHERE id = #{id}
