mapper.select:
SELECT 

FROM agency_negotiate_record T

WHERE T.id = #{id}

mapper.update:
UPDATE agency_negotiate_record SET 	
	associd = #{associd},
	content = #{content},
	replyuserid = #{replyuserid},
	replytime = #{replytime}
WHERE id = #{id}

update agency_negotiate_record set 
	deleted=1
WHERE id = #{id}
