mapper.select:
SELECT 

FROM interaction_notice_record T



		 T.deleted=0


				AND	T.content LIKE '%${condition.model.content}%'


				AND	T.type = #{condition.model.type}


				AND	T.status = #{condition.model.status}



			AND T.receiveuser = #{condition.receiveuser} 


	order by T.ID DESC

SELECT 
	count(*)
FROM interaction_notice_record T



		 T.deleted=0


				AND	T.content LIKE '%${condition.model.content}%'


				AND	T.type = #{condition.model.type}


				AND	T.status = #{condition.model.status}



			AND T.receiveuser = #{condition.receiveuser}

mapper.update:
update interaction_notice_record set 
	deleted=1
WHERE id = #{id}

update interaction_notice_record set 
      status = #{status}
WHERE id = #{id}

update interaction_notice_record set 
      status =1
WHERE id = #{id}

update interaction_notice_record set 
      status =-1
WHERE id = #{id}
